#!/usr/bin/env python
# encoding: utf-8

import cv2
import Image

def GaussianMake(imgName):
    k_size = 15
    kernal_size = (k_size, k_size)
    sigma = 0

    img = cv2.imread(imgName)
    img = cv2.GaussianBlur(img, kernal_size, sigma)
    new_imgName = "b_" + imgName
    cv2.imwrite(new_imgName, img)

    im = Image.open(new_imgName)
    im = im.resize((500, 500), Image.ANTIALIAS)
    im.save(new_imgName)

maxSize = input("MaxSize:")
for idx in range(maxSize):
    Name = str(idx) + '.jpg'
    GaussianMake(Name)



